           sum += storeGrades[i];
       }
       return sum;
   }

C) public static int getTotalScore(int[] storeGrades) {
       int sum = 0;
       for (int grade : storeGrades) {
           sum += grade;
       }
       return sum;
   }

D) public static int getTotalScore(int[] storeGrades) {
