                if($diff>0) {
                    $item->setCustomPrice($price);
                    $item->setOriginalCustomPrice($price);
                    $item->getProduct()->setIsSuperMode(true);
                } else if($diff<=0) {
                    if($current_qty > $avlqty) {
                        $item->setCustomPrice($price);
                        $item->setOriginalCustomPrice($price);
                        $item->setQty($avlqty);
